CREATE TABLE AIR_TRAFFIC_CONTROLLER(	
	HOME_ATC_WORKER_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	SALARY NUMBER(*,0) NOT NULL, 
	NAME_OF_WORKER VARCHAR2(255) NOT NULL, 
	ID_OF_TOWER NUMBER(*,0) NOT NULL, 
	LOCATION VARCHAR2(255) NOT NULL
   );

CREATE TABLE AIRPLANE (	
	AIRPLANE_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	NUMBER_ NUMBER(*,0) NOT NULL, 
	PILOT_ID NUMBER(*,0) NOT NULL, 
	NAME_OF_PILOT VARCHAR2(255) NOT NULL, 
	AMOUNT_OF_FUEL NUMBER(*,0) NOT NULL, 
	AIRPLANE_TYPE VARCHAR2(255) NOT NULL, 
	WEIGHT_OF_LUGGAGE NUMBER(*,0) NOT NULL, 
	PASSENGER_ID NUMBER(*,0) NOT NULL, 
	DESTINATION_TOWER_ID NUMBER(*,0) NOT NULL, 
	HOME_ATC_WORKER_ID NUMBER(*,0) NOT NULL,
	HANGAR_ID NUMBER(*,0) NOT NULL,
	DESTINATION VARCHAR(255) NOT NULL
   );

CREATE TABLE DRIVER(
	DRIVER_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	SALARY NUMBER(*,0) NOT NULL, 
	NAME_OF_DRIVER VARCHAR2(255) NOT NULL, 
	DRIVERS_LICENCE VARCHAR2(255) NOT NULL, 
	TIME_OF_SCHIFT VARCHAR2(255) NOT NULL, 
	VEHICLE_ID NUMBER(*,0) NOT NULL
   );

CREATE TABLE HANGAR (
	HANGAR_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	POSITION_IN_AIRPORT_COMPLEX VARCHAR2(255), 
	MECHANIC_ID NUMBER(*,0), 
	DRIVER_ID NUMBER(*,0), 
	PILOT_ID NUMBER(*,0),
	NUMBER_ NUMBER(*,0), 
	VEHICLE_ID NUMBER(*,0),
	AIRPLANE_ID NUMBER(*,0)
   );

CREATE TABLE CHECK_IN_WORKER (
	WORKER_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	SALARY NUMBER(*,0) NOT NULL, 
	NAME_OF_WORKER VARCHAR2(255) NOT NULL, 
	CHECK_IN_STATION NUMBER(*,0) NOT NULL, 
	PASSENGER_ID NUMBER(*,0) NOT NULL, 
	TIME_OF_SCHIFT VARCHAR2(255) NOT NULL
   );


CREATE TABLE LAND_VEHICLE (
	VEHICLE_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	NUMBER_ NUMBER(*,0) NOT NULL, 
	DRIVER_ID NUMBER(*,0) NOT NULL, 
	NAME_OF_DRIVER VARCHAR2(255) NOT NULL, 
	AMOUNT_OF_FUEL NUMBER(*,0) NOT NULL, 
	VEHICLE_TYPE VARCHAR2(255) NOT NULL,
	HANGAR_ID NUMBER(*,0) NOT NULL
   );
   


CREATE TABLE LUGGAGE (
	LUGGAGE_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	NAME_OF_OWNER VARCHAR2(255) NOT NULL
   );


CREATE TABLE MECHANIC(
	MECHANIC_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	SALARY NUMBER(*,0) NOT NULL, 
	NAME_OF_MECHANIC VARCHAR2(255) NOT NULL, 
	VEHICLE_ID NUMBER(*,0) NOT NULL, 
	TIME_OF_SCHIFT VARCHAR2(255) NOT NULL,
	AIRPLANE_ID NUMBER(*,0) NOT NULL
   );

CREATE TABLE PASSENGER(
	PASSENGER_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	TICKET_ID NUMBER(*,0) NOT NULL, 
	SECURITY_CLEARANCE VARCHAR2(255) NOT NULL, 
	NAME_OF_PASSENGER VARCHAR2(255) NOT NULL, 
	LUGGAGE_ID NUMBER(*,0) NOT NULL,
	TIME_OF_DEPARTURE VARCHAR2(255) NOT NULL
   );

CREATE TABLE PILOT(
	PILOT_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	SALARY NUMBER(*,0) NOT NULL, 
	NAME_OF_PILOT VARCHAR2(255) NOT NULL, 
	AIRPLANE_ID NUMBER(*,0) NOT NULL, 
	TIME_OF_SCHIFT VARCHAR2(255) NOT NULL
   );

CREATE TABLE PLANES_DESTINATION_ATCT (
	DESTINATION_TOWER_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	PLANES_INFORMATIONS VARCHAR2(255) NOT NULL, 
	HOME_ATC_WORKER_ID NUMBER(*,0) NOT NULL
   );

CREATE TABLE SECURITY_WORKER (
	WORKER_ID NUMBER(*,0) PRIMARY KEY NOT NULL, 
	SALARY NUMBER(*,0) NOT NULL, 
	NAME_OF_WORKER VARCHAR2(255) NOT NULL, 
	SECURITY_STATION NUMBER(*,0) NOT NULL, 
	PASSENGER_ID NUMBER(*,0) NOT NULL, 
	TIME_OF_SCHIFT VARCHAR2(255) NOT NULL
   );

